package com.iflytek;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.context.embedded.EmbeddedServletContainerCustomizer;import org.springframework.boot.web.servlet.ErrorPage;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.PropertySource;import org.springframework.http.HttpStatus;/** * @author: demopoo * @Date: Created in 下午4:14 2018/4/16 * @Des: * @Modifyed By: */@PropertySource(value = {"classpath:config.properties"})@SpringBootApplicationpublic class ApplicationStart {    public static void main(String[] args) {        SpringApplication.run(ApplicationStart.class,args);    }    @Bean    public EmbeddedServletContainerCustomizer containerCustomizer() {        return (container -> {            ErrorPage error404Page = new ErrorPage(HttpStatus.NOT_FOUND, "/errors/404.html");            container.addErrorPages(error404Page);        });    }}