package com.iflytek.config;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.filter.CharacterEncodingFilter;/** * @author: demopoo * @Date: Created in 下午2:00 2018/4/17 * @Des: 设置字符集编码 * @Modifyed By: */@Configurationpublic class CharacterEncodingFilterConfig {    @Bean    public FilterRegistrationBean filterRegistrationBean(){        FilterRegistrationBean registrationBean = new FilterRegistrationBean();        CharacterEncodingFilter characterEncodingFilter = new CharacterEncodingFilter();        characterEncodingFilter.setForceEncoding(true);        characterEncodingFilter.setEncoding("GBK");        registrationBean.setFilter(characterEncodingFilter);        return registrationBean;    }}