package com.iflytek.config;import org.springframework.core.convert.converter.Converter;import org.springframework.core.serializer.support.DeserializingConverter;import org.springframework.core.serializer.support.SerializingConverter;import org.springframework.data.redis.serializer.RedisSerializer;import org.springframework.data.redis.serializer.SerializationException;/** * @author: demopoo * @Date: Created in 下午5:51 2018/4/16 * @Des: * @Modifyed By: */public class RedisObjectSerialize implements RedisSerializer<Object> {    public static final byte[] EMPTY_BYTE = new byte[0];    public Converter<Object, byte[]> serializingConverter = new SerializingConverter();    public Converter<byte[],Object> deserializingConverter = new DeserializingConverter();    public byte[] serialize(Object o) throws SerializationException {        if (o == null){            return EMPTY_BYTE;        }        return serializingConverter.convert(o);    }    public Object deserialize(byte[] bytes) throws SerializationException {        if (bytes == null || bytes.length == 0){            return null;        }        return deserializingConverter.convert(bytes);    }}