package com.iflytek.config.hbase;import com.iflytek.dao.HBaseDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.context.properties.EnableConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * @author: demopoo * @Date: Created in 上午9:55 2018/4/19 * @Des: Hbase的配置文件 * @Modifyed By: */@Configuration@EnableConfigurationProperties(HbaseProperties.class)public class HbaseConfig {    @Autowired    private HbaseProperties hbaseProperties;    @Bean    public HBaseDao hBaseDao(){        return new HBaseDao(this.hbaseProperties.getMaster(),this.hbaseProperties.getZkQuorum(),                this.hbaseProperties.getZkPort(),this.hbaseProperties.getZkBasePath(),this.hbaseProperties.getPoolSize());    }}