package com.iflytek.entity;import com.iflytek.entity.req.RecommendCommonParams;import com.iflytek.entity.res.RecommendCommonResult;import com.iflytek.entity.res.ResponseContent;import com.iflytek.entity.res.ResponseInfo;import com.iflytek.util.DateUtils;import java.util.Date;/** * @author: demopoo * @Date: Created in 下午2:23 2018/4/17 * @Des: mock 返回数据 * @Modifyed By: */public class MockResult {    /**     *     * @param acceptId 接入编号     * @param responseInfo     * @param responseContent     * @return     */    public static RecommendCommonResult getRecommendCommonResult(String acceptId,ResponseInfo responseInfo, ResponseContent responseContent){        RecommendCommonResult result = new RecommendCommonResult();        result.setAccept_id(acceptId);        result.setResponse_time(DateUtils.date2Str(new Date(),DateUtils.YYYYDDHHMMSS));        result.setResponse(responseInfo);        result.setContent(responseContent);        return result;    }    /**     * 返回成功结果     * @param params     * @param responseContent     * @return     */    public static RecommendCommonResult returnSuccessResult(RecommendCommonParams params,ResponseContent responseContent){        ResponseInfo info = new ResponseInfo();        info.setResp_code("0000");        info.setResp_desc("NORMAL_OK");        info.setResp_result(0);        RecommendCommonResult result = new RecommendCommonResult();        result.setAccept_id(params.getAccept_info().getAccept_id());        result.setResponse_time(DateUtils.date2Str(new Date(),DateUtils.YYYYDDHHMMSS));        result.setResponse(info);        result.setContent(responseContent);        return result;    }    /**     * 返回错误信息     * @param params     * @param msg     * @param code     * @return     */    public static RecommendCommonResult returnErrorResult(RecommendCommonParams params,String msg,String code){        ResponseInfo info = new ResponseInfo();        info.setResp_code(code);        info.setResp_desc(msg);        info.setResp_result(1);        RecommendCommonResult result = new RecommendCommonResult();        result.setAccept_id(params.getAccept_info().getAccept_id());        result.setResponse_time(DateUtils.date2Str(new Date(),DateUtils.YYYYDDHHMMSS));        result.setResponse(info);        return result;    }}