package com.iflytek.entity.res;import com.fasterxml.jackson.dataformat.xml.annotation.JacksonXmlElementWrapper;import com.fasterxml.jackson.dataformat.xml.annotation.JacksonXmlProperty;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;/** * @author: demopoo * @Date: Created in 上午12:00 2018/4/17 * @Des: * @Modifyed By: */@Data@AllArgsConstructor@NoArgsConstructorpublic class ResponseContent {    @JacksonXmlElementWrapper(localName="results")    @JacksonXmlProperty(localName="result")    private List<ResponseItem> results;    public List<ResponseItem> getResults() {        return results;    }    public void setResults(List<ResponseItem> results) {        this.results = results;    }}