package com.iflytek.util;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.MessageSource;import org.springframework.stereotype.Component;import javax.annotation.Resource;import java.util.Locale;/** * @author: demopoo * @Date: Created in 下午6:32 2018/4/18 * @Des: 配置文件工具 * @Modifyed By: */@Componentpublic class ConfigUtils {    @Resource    private MessageSource messageSource;    public String getUrl(String name,String... args){        return messageSource.getMessage(name,args, Locale.getDefault());    }}